import React, { Component } from 'react';
import './signup.css';

class Signup extends Component {
    submitData = (event) => {
        event.preventDefault();

        const name = event.target.elements.name.value;
        const email = event.target.elements.email.value;
        const password = event.target.elements.password.value;

        console.log("Name:", name);
        console.log("Email:", email);
        console.log("Password:", password);
    }

    render() {
        return (
            <div className='loginpage-container'>
                <div className='login-container'>
                    <h1 className='login-heading'>Create<br /> New Account</h1>
                    <form onSubmit={this.submitData}>
                        <label className='loginpage-label'>Name</label><br />
                        <input className='loginpage-input' type='text' name='name' /><br />
                        <label className='loginpage-label'>Email</label><br />
                        <input className='loginpage-input' type='text' name='email' /><br />
                        <label className='loginpage-label'>Password</label><br />
                        <input className='loginpage-input' type='password' name='password' /><br />
                        <button className='loginpage-submitbutton' type='submit'>Submit</button><br />
                        <label className="signup-redirecting">We Have Account <a href='/login'>Login</a></label>
                    </form>
                </div>
            </div>
        )
    }
}

export default Signup;
